mapper.select:
SELECT COUNT(*) sumcount,t2.*,t4.show_url imgurl,t5.g_unitname,t3.gd_price gdPrice from t_goods_ggsval_detail t1 LEFT JOIN t_goods_gdcprel t2 ON t1.gd_id=t2.gd_id and t2.is_delete!=1 LEFT JOIN t_goods_detail t3 ON t3.gd_id=t2.gd_id and t3.is_delete!=1 LEFT JOIN t_attachment t4 ON t3.gd_imgid=t4.show_url LEFT JOIN t_goods t5 ON t5.g_id=t3.g_id and t5.is_delete!=1, t_cut_price t6 where t1.is_delete!=1 and t1.g_id=#{params[gid]} and t1.gsv_id IN
#{gsvid}
and t6.cp_id=t2.cp_id and t6.parent_id=#{params[parentId]} and t6.is_delete=0 GROUP BY t1.gd_id ORDER BY sumcount DESC

SELECT * FROM t_goods_gdcprel WHERE gdcp_id in
#{gdcpId}
and is_delete=0
