mapper.select:
SELECT t1.*, t2.tm_name clientName,t2.tm_phone clientPhone, t3.wau_headimgurl headImgUrl, t4.em_name empName FROM t_check_user t1, t_client_manage t2 LEFT JOIN t_wx_auth_user t3 ON t2.tm_id=t3.wau_u_id, t_employee_manage t4
WHERE t1.is_delete=0 and t2.is_delete=0 and t4.is_delete=0 and t1.c_id=t2.tm_id and t1.o_id=#{params.oid} and t2.tm_employee=t4.em_id
and t1.is_use=#{params.isUse}
and t2.tm_employee in
#{params.empIds[${index}]}
and t4.em_team in
#{params.tids[${index}]}
and t2.tm_employee=#{params.eid}

SELECT t4.t_id tid,t4.t_name tname, t3.em_name empName,t3.em_id empId, count(*) count FROM t_check_user t1, t_client_manage t2, t_employee_manage t3, t_team t4 WHERE t1.is_delete=0 and t1.o_id=#{params.oid} and t2.is_delete=0 and t3.is_delete=0 and t4.is_delete=0 and t4.t_id=t3.em_team and t1.c_id=t2.tm_id and t2.tm_employee=t3.em_id
and t4.t_id in
#{tid}
and t3.em_id IN
#{emId}
and t1.is_use=#{params.isUse}

SELECT t1.em_name empName,t1.em_id empId, t3.t_id tid,t3.t_name tname, COUNT(t4.c_id) count FROM t_employee_manage t1 LEFT JOIN t_client_manage t2 ON t1.em_id=t2.tm_employee and t2.is_delete=0 and t2.tm_login_status=0 LEFT JOIN t_check_user t4 ON t2.tm_id=t4.c_id and t4.is_delete=0 and t4.o_id=#{params.oid} and t4.is_use=#{params.isUse}, t_team t3 WHERE t3.t_id=t1.em_team and t1.is_delete=0 and t3.t_id IN
#{tid}
GROUP BY t1.em_id

SELECT t1.em_name empName,t1.em_id empId, t3.t_id tid,t3.t_name tname, COUNT(t4.c_id) count FROM t_employee_manage t1 LEFT JOIN t_client_manage t2 ON t1.em_id=t2.tm_employee and t2.is_delete=0 and t2.tm_login_status=0 LEFT JOIN t_check_user t4 ON t2.tm_id=t4.c_id and t4.is_delete=0 and t4.o_id=#{params.oid} and t4.is_use=#{params.isUse}, t_team t3 WHERE t3.t_id=t1.em_team and t1.is_delete=0 and t1.em_id IN
#{emId}
GROUP BY t1.em_id
